INSERT INTO `wp_t_questions` (`question_id`, `test_id`, `question_title`) VALUES
(1, 93, 'Sueles anhelar la emoción?'),
(2, 93, 'Do you often need understanding friends to cheer you up?'),
(3, 93, 'Are you usually carefree?'),
(4, 93, 'Do you find it very hard to take no for an answer?'),
(5, 93, 'Do you stop and think things over before doing anything?'),
(6, 93, 'If you say you will do something do you always keep your promise, no matter how inconvenient it might be to do so?'),
(7, 93, 'Do your moods go up and down?'),
(8, 93, 'Do you generally do and say things quickly without stopping to think?'),
(9, 93, 'Do you ever feel ‘just miserable’ for no good reason?'),
(10, 93, 'Would you do almost anything for a dare?'),
(11, 93, 'Do you suddenly feel shy when you want to talk to an attractive stranger?'),
